inputs.content:
body {
    font-family: Arial, sans-serif;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    margin: 0;
    background-color: #faf8ef;
}
.container {
    text-align: center;
}
h1 {
    color: #776e65;
    font-size: 48px;
    margin-bottom: 10px;
}
.score-container {
    font-size: 24px;
    color: #776e65;
    margin-bottom: 20px;
}
.grid {
    display: grid;
    grid-template-columns: repeat(4, 100px);
    grid-template-rows: repeat(4, 100px);
    gap: 10px;
    background-color: #bbada0;
    padding: 10px;
    border-radius: 6px;
    margin: 0 auto 20px;
}
.tile {
    width: 100px;
    height: 100px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 36px;
    font-weight: bold;
    border-radius: 6px;
    background-color: #cdc1b4;
    color: #776e65;
}
.tile-2 { background-color: #eee4da; }
.tile-4 { background-color: #ede0c8; }
.tile-8 { background-color: #f2b179; color: #f9f6f2; }
.tile-16 { background-color: #f59563; color: #f9f6f2; }
.tile-32 { background-color: #f67c5f; color: #f9f6f2; }
.tile-64 { background-color: #f65e3b; color: #f9f6f2; }
.tile-128 { background-color: #edcf72; color: #f9f6f2; }
.tile-256 { background-color: #edcc61; color: #f9f6f2; }
.tile-512 { background-color: #edc850; color: #f9f6f2; }
.tile-1024 { background-color: #edc53f; color: #f9f6f2; }
.tile-2048 { background-color: #edc22e; color: #f9f6f2; }
.instructions {
    color: #776e65;
    font-size: 18px;
    margin-top: 20px;
}

const gridSize = 4;
let grid = [];
let score = 0;

function initGrid() {
    grid = Array(gridSize).fill().map(() => Array(gridSize).fill(0));
    addRandomTile();
    addRandomTile();
    renderGrid();
}

function addRandomTile() {
    let emptyCells = [];
    for (let i = 0; i < gridSize; i++) {
        for (let j = 0; j < gridSize; j++) {
            if (grid[i][j] === 0) emptyCells.push({x: i, y: j});
        }
    }
    if (emptyCells.length > 0) {
        let cell = emptyCells[Math.floor(Math.random() * emptyCells.length)];
        grid[cell.x][cell.y] = Math.random() < 0.9 ? 2 : 4;
    }
}

function renderGrid() {
    const gridElement = document.getElementById('grid');
    gridElement.innerHTML = '';
    for (let i = 0; i < gridSize; i++) {
        for (let j = 0; j < gridSize; j++) {
            const tile = document.createElement('div');
            tile.className = 'tile';
            if (grid[i][j] !== 0) {
                tile.textContent = grid[i][j];
                tile.classList.add(`tile-${grid[i][j]}`);
            }
            gridElement.appendChild(tile);
        }
    }
    document.getElementById('score').textContent = score;
}

function move(direction) {
    let moved = false;
    const oldGrid = grid.map(row => [...row]);

    if (direction === 'left' || direction === 'right') {
        for (let i = 0; i < gridSize; i++) {
            let row = grid[i].filter(val => val !== 0);
            if (direction === 'right') row.reverse();
            for (let j = 0; j < row.length - 1; j++) {
                if (row[j] === row[j + 1]) {
                    row[j] *= 2;
                    score += row[j];
                    row.splice(j + 1, 1);
                }
            }
            while (row.length < gridSize) row.push(0);
            if (direction === 'right') row.reverse();
            grid[i] = row;
        }
    } else if (direction === 'up' || direction === 'down') {
        for (let j = 0; j < gridSize; j++) {
            let col = grid.map(row => row[j]).filter(val => val !== 0);
            if (direction === 'down') col.reverse();
            for (let i = 0; i < col.length - 1; i++) {
                if (col[i] === col[i + 1]) {
                    col[i] *= 2;
                    score += col[i];
                    col.splice(i + 1, 1);
                }
            }
            while (col.length < gridSize) col.push(0);
            if (direction === 'down') col.reverse();
            for (let i = 0; i < gridSize; i++) grid[i][j] = col[i];
        }
    }

    for (let i = 0; i < gridSize; i++) {
        for (let j = 0; j < gridSize; j++) {
            if (grid[i][j] !== oldGrid[i][j]) moved = true;
        }
    }
    if (moved) addRandomTile();
    renderGrid();
}

document.addEventListener('keydown', (e) => {
    switch (e.key) {
        case 'ArrowLeft': move('left'); break;
        case 'ArrowRight': move('right'); break;
        case 'ArrowUp': move('up'); break;
        case 'ArrowDown': move('down'); break;
    }
});

initGrid();
