inputs.content:
body {
    font-family: Arial, sans-serif;
    background-color: #faf8ef;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    margin: 0;
}
.container {
    text-align: center;
}
h1 {
    color: #776e65;
    font-size: 48px;
    margin-bottom: 20px;
}
.score-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
}
.score {
    font-size: 24px;
    color: #776e65;
}
#restart {
    background-color: #8f7a66;
    color: white;
    border: none;
    padding: 10px 20px;
    font-size: 18px;
    border-radius: 5px;
    cursor: pointer;
}
#restart:hover {
    background-color: #7c6b5a;
}
.grid {
    display: grid;
    grid-template-columns: repeat(4, 100px);
    grid-template-rows: repeat(4, 100px);
    gap: 10px;
    background-color: #bbada0;
    padding: 10px;
    border-radius: 10px;
    margin: 0 auto 20px;
}
.tile {
    width: 100px;
    height: 100px;
    background-color: #cdc1b4;
    border-radius: 5px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 36px;
    font-weight: bold;
    color: #776e65;
}
.tile-2 { background-color: #eee4da; }
.tile-4 { background-color: #ede0c8; }
.tile-8 { background-color: #f2b179; color: white; }
.tile-16 { background-color: #f59563; color: white; }
.tile-32 { background-color: #f67c5f; color: white; }
.tile-64 { background-color: #f65e3b; color: white; }
.tile-128 { background-color: #edcf72; color: white; }
.tile-256 { background-color: #edcc61; color: white; }
.tile-512 { background-color: #edc850; color: white; }
.tile-1024 { background-color: #edc53f; color: white; }
.tile-2048 { background-color: #edc22e; color: white; }
.instructions {
    color: #776e65;
    font-size: 16px;
    max-width: 400px;
    margin: 0 auto;
}

const gridSize = 4;
let grid = [];
let score = 0;

function initGrid() {
    grid = Array(gridSize).fill().map(() => Array(gridSize).fill(0));
    addRandomTile();
    addRandomTile();
    updateGrid();
}

function addRandomTile() {
    let emptyCells = [];
    for (let r = 0; r < gridSize; r++) {
        for (let c = 0; c < gridSize; c++) {
            if (grid[r][c] === 0) emptyCells.push({r, c});
        }
    }
    if (emptyCells.length > 0) {
        let {r, c} = emptyCells[Math.floor(Math.random() * emptyCells.length)];
        grid[r][c] = Math.random() < 0.9 ? 2 : 4;
    }
}

function updateGrid() {
    const gridElement = document.getElementById('grid');
    gridElement.innerHTML = '';
    for (let r = 0; r < gridSize; r++) {
        for (let c = 0; c < gridSize; c++) {
            const tile = document.createElement('div');
            tile.className = 'tile';
            if (grid[r][c] !== 0) {
                tile.textContent = grid[r][c];
                tile.classList.add(`tile-${grid[r][c]}`);
            }
            gridElement.appendChild(tile);
        }
    }
    document.getElementById('score').textContent = score;
}

function move(direction) {
    let moved = false;
    const oldGrid = grid.map(row => [...row]);

    if (direction === 'left' || direction === 'right') {
        for (let r = 0; r < gridSize; r++) {
            let row = grid[r].filter(val => val !== 0);
            if (direction === 'right') row.reverse();
            for (let i = 0; i < row.length - 1; i++) {
                if (row[i] === row[i + 1]) {
                    row[i] *= 2;
                    score += row[i];
                    row.splice(i + 1, 1);
                }
            }
            while (row.length < gridSize) row.push(0);
            if (direction === 'right') row.reverse();
            grid[r] = row;
        }
    } else if (direction === 'up' || direction === 'down') {
        for (let c = 0; c < gridSize; c++) {
            let col = [];
            for (let r = 0; r < gridSize; r++) col.push(grid[r][c]);
            col = col.filter(val => val !== 0);
            if (direction === 'down') col.reverse();
            for (let i = 0; i < col.length - 1; i++) {
                if (col[i] === col[i + 1]) {
                    col[i] *= 2;
                    score += col[i];
                    col.splice(i + 1, 1);
                }
            }
            while (col.length < gridSize) col.push(0);
            if (direction === 'down') col.reverse();
            for (let r = 0; r < gridSize; r++) grid[r][c] = col[r];
        }
    }

    for (let r = 0; r < gridSize; r++) {
        for (let c = 0; c < gridSize; c++) {
            if (grid[r][c] !== oldGrid[r][c]) moved = true;
        }
    }
    if (moved) addRandomTile();
    updateGrid();
}

function handleKeyPress(event) {
    switch (event.key) {
        case 'ArrowLeft': move('left'); break;
        case 'ArrowRight': move('right'); break;
        case 'ArrowUp': move('up'); break;
        case 'ArrowDown': move('down'); break;
    }
}

function restartGame() {
    score = 0;
    initGrid();
}

document.addEventListener('DOMContentLoaded', () => {
    initGrid();
    document.addEventListener('keydown', handleKeyPress);
    document.getElementById('restart').addEventListener('click', restartGame);
});
