inputs.content:
body {
    font-family: Arial, sans-serif;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    margin: 0;
    background-color: #faf8ef;
}
.container {
    text-align: center;
}
h1 {
    color: #776e65;
    font-size: 48px;
    margin-bottom: 10px;
}
.score-container {
    font-size: 24px;
    color: #776e65;
    margin-bottom: 20px;
}
.grid {
    display: grid;
    grid-template-columns: repeat(4, 100px);
    grid-template-rows: repeat(4, 100px);
    gap: 10px;
    background-color: #bbada0;
    padding: 10px;
    border-radius: 6px;
    margin: 0 auto 20px;
}
.tile {
    width: 100px;
    height: 100px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 36px;
    font-weight: bold;
    border-radius: 6px;
    background-color: #cdc1b4;
    color: #776e65;
}
.tile-2 { background-color: #eee4da; }
.tile-4 { background-color: #ede0c8; }
.tile-8 { background-color: #f2b179; color: #f9f6f2; }
.tile-16 { background-color: #f59563; color: #f9f6f2; }
.tile-32 { background-color: #f67c5f; color: #f9f6f2; }
.tile-64 { background-color: #f65e3b; color: #f9f6f2; }
.tile-128 { background-color: #edcf72; color: #f9f6f2; }
.tile-256 { background-color: #edcc61; color: #f9f6f2; }
.tile-512 { background-color: #edc850; color: #f9f6f2; }
.tile-1024 { background-color: #edc53f; color: #f9f6f2; }
.tile-2048 { background-color: #edc22e; color: #f9f6f2; }
.instructions {
    font-size: 16px;
    color: #776e65;
    margin-bottom: 20px;
}
button {
    padding: 10px 20px;
    font-size: 18px;
    background-color: #8f7a66;
    color: #f9f6f2;
    border: none;
    border-radius: 6px;
    cursor: pointer;
}
button:hover {
    background-color: #9f8b77;
}

document.addEventListener('DOMContentLoaded', () => {
    const gridSize = 4;
    let grid = [];
    let score = 0;
    const gridElement = document.getElementById('grid');
    const scoreElement = document.getElementById('score');
    const restartButton = document.getElementById('restart');

    function initGrid() {
        grid = Array(gridSize).fill().map(() => Array(gridSize).fill(0));
        addRandomTile();
        addRandomTile();
        renderGrid();
    }

    function addRandomTile() {
        const emptyCells = [];
        for (let r = 0; r < gridSize; r++) {
            for (let c = 0; c < gridSize; c++) {
                if (grid[r][c] === 0) emptyCells.push({ r, c });
            }
        }
        if (emptyCells.length > 0) {
            const { r, c } = emptyCells[Math.floor(Math.random() * emptyCells.length)];
            grid[r][c] = Math.random() < 0.9 ? 2 : 4;
        }
    }

    function renderGrid() {
        gridElement.innerHTML = '';
        for (let r = 0; r < gridSize; r++) {
            for (let c = 0; c < gridSize; c++) {
                const tile = document.createElement('div');
                tile.className = 'tile';
                if (grid[r][c] !== 0) {
                    tile.textContent = grid[r][c];
                    tile.classList.add(`tile-${grid[r][c]}`);
                }
                gridElement.appendChild(tile);
            }
        }
        scoreElement.textContent = score;
    }

    function move(direction) {
        let moved = false;
        const oldGrid = grid.map(row => [...row]);
        if (direction === 'left') {
            for (let r = 0; r < gridSize; r++) {
                const row = grid[r].filter(val => val !== 0);
                for (let i = 0; i < row.length - 1; i++) {
                    if (row[i] === row[i + 1]) {
                        row[i] *= 2;
                        score += row[i];
                        row.splice(i + 1, 1);
                    }
                }
                while (row.length < gridSize) row.push(0);
                grid[r] = row;
            }
        } else if (direction === 'right') {
            for (let r = 0; r < gridSize; r++) {
                const row = grid[r].filter(val => val !== 0);
                for (let i = row.length - 1; i > 0; i--) {
                    if (row[i] === row[i - 1]) {
                        row[i] *= 2;
                        score += row[i];
                        row.splice(i - 1, 1);
                        i--;
                    }
                }
                while (row.length < gridSize) row.unshift(0);
                grid[r] = row;
            }
        } else if (direction === 'up') {
            for (let c = 0; c < gridSize; c++) {
                const column = [];
                for (let r = 0; r < gridSize; r++) column.push(grid[r][c]);
                const filtered = column.filter(val => val !== 0);
                for (let i = 0; i < filtered.length - 1; i++) {
                    if (filtered[i] === filtered[i + 1]) {
                        filtered[i] *= 2;
                        score += filtered[i];
                        filtered.splice(i + 1, 1);
                    }
                }
                while (filtered.length < gridSize) filtered.push(0);
                for (let r = 0; r < gridSize; r++) grid[r][c] = filtered[r];
            }
        } else if (direction === 'down') {
            for (let c = 0; c < gridSize; c++) {
                const column = [];
                for (let r = 0; r < gridSize; r++) column.push(grid[r][c]);
                const filtered = column.filter(val => val !== 0);
                for (let i = filtered.length - 1; i > 0; i--) {
                    if (filtered[i] === filtered[i - 1]) {
                        filtered[i] *= 2;
                        score += filtered[i];
                        filtered.splice(i - 1, 1);
                        i--;
                    }
                }
                while (filtered.length < gridSize) filtered.unshift(0);
                for (let r = 0; r < gridSize; r++) grid[r][c] = filtered[r];
            }
        }
        for (let r = 0; r < gridSize; r++) {
            for (let c = 0; c < gridSize; c++) {
                if (grid[r][c] !== oldGrid[r][c]) moved = true;
            }
        }
        if (moved) addRandomTile();
        renderGrid();
    }

    document.addEventListener('keydown', (e) => {
        switch (e.key) {
            case 'ArrowLeft': move('left'); break;
            case 'ArrowRight': move('right'); break;
            case 'ArrowUp': move('up'); break;
            case 'ArrowDown': move('down'); break;
        }
    });

    restartButton.addEventListener('click', () => {
        score = 0;
        initGrid();
    });

    initGrid();
});
