inputs.content:
body {
    font-family: Arial, sans-serif;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    margin: 0;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}
.container {
    text-align: center;
    background: white;
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0 4px 6px rgba(0,0,0,0.1);
}
h1 {
    color: #333;
}
.score {
    font-size: 20px;
    margin: 10px 0;
}
.grid {
    display: grid;
    grid-template-columns: repeat(4, 100px);
    grid-gap: 10px;
    margin: 20px auto;
}
.cell {
    width: 100px;
    height: 100px;
    background: #eee4da;
    border-radius: 5px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 24px;
    font-weight: bold;
    color: #776e65;
}
button {
    padding: 10px 20px;
    font-size: 16px;
    background: #8f7a66;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
}
button:hover {
    background: #7c6b5a;
}

let grid = [];
let score = 0;
const gridSize = 4;

function initGrid() {
    grid = Array(gridSize).fill().map(() => Array(gridSize).fill(0));
    addRandomTile();
    addRandomTile();
    renderGrid();
}

function addRandomTile() {
    let emptyCells = [];
    for (let i = 0; i < gridSize; i++) {
        for (let j = 0; j < gridSize; j++) {
            if (grid[i][j] === 0) emptyCells.push({x: i, y: j});
        }
    }
    if (emptyCells.length > 0) {
        let cell = emptyCells[Math.floor(Math.random() * emptyCells.length)];
        grid[cell.x][cell.y] = Math.random() < 0.9 ? 2 : 4;
    }
}

function renderGrid() {
    const gridElement = document.getElementById('grid');
    gridElement.innerHTML = '';
    for (let i = 0; i < gridSize; i++) {
        for (let j = 0; j < gridSize; j++) {
            let cell = document.createElement('div');
            cell.className = 'cell';
            cell.textContent = grid[i][j] === 0 ? '' : grid[i][j];
            gridElement.appendChild(cell);
        }
    }
    document.getElementById('score').textContent = score;
}

function move(direction) {
    // Simplified move logic for basic functionality
    let moved = false;
    // This is a placeholder; implement full 2048 logic as needed
    if (direction === 'up') {
        // Add actual move logic here
    }
    if (moved) {
        addRandomTile();
        renderGrid();
    }
}

function resetGame() {
    score = 0;
    initGrid();
}

// Initialize game
initGrid();
// Add keyboard controls
document.addEventListener('keydown', (e) => {
    switch(e.key) {
        case 'ArrowUp': move('up'); break;
        case 'ArrowDown': move('down'); break;
        case 'ArrowLeft': move('left'); break;
        case 'ArrowRight': move('right'); break;
    }
});
